import React from "react";
import { Link, Redirect } from "react-router-dom";
import Axios from "axios";

class AddEventForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      eventname: "",
      description: "",
      eventtime: "",
      eventdate: "",
      location: "",
      eligibility: "",
      authemail: "",
      redirector: null
    };
    //this.redirector="";
    this.onTextChangeHandler = this.onTextChangeHandler.bind(this);
    this.onSubmitHandler = this.onSubmitHandler.bind(this);
  }
  onTextChangeHandler(event) {
    this.setState({ [event.target.name]: event.target.value });
  }

  async onSubmitHandler(event) {
    event.preventDefault();
    let email = localStorage.getItem("email");
    await this.setState({ authemail: email });
    Axios.post("http://127.0.0.1:4001/addeventform", this.state)
      .then(data => {
        console.log("data");
        this.setState({
          redirector: "/CompanyDashboard/CompEvents/Pages/CompEvents"
        });
      })
      .catch(err => {
        console.log("error");
      });
  }

  render() {
    if (this.state.redirector) return <Redirect to={this.state.redirector} />;
    return (
      <div>
        <div class="container">
          <form>
            <div class="row">
              <div class="col-25">
                <label for="eventname">Event Name : </label>
              </div>
              <div class="col-75">
                <input
                  type="text"
                  name="eventname"
                  placeholder="Graduation Job Fair 2020"
                  onChange={this.onTextChangeHandler}
                  required
                />
              </div>
            </div>

            <div class="row">
              <div class="col-25">
                <label for="description">Description : </label>
              </div>
              <div class="col-75">
                <textarea
                  name="description"
                  rows="1"
                  cols="100"
                  placeholder="some details about the event"
                  onChange={this.onTextChangeHandler}
                ></textarea>
              </div>
            </div>

            <div class="row">
              <div class="col-25">
                <label for="eventdate">Date: </label>
              </div>
              <div class="col-75">
                <input
                  type="date"
                  name="eventdate"
                  required
                  onChange={this.onTextChangeHandler}
                />
              </div>
            </div>

            <div class="row">
              <div class="col-25">
                <label for="eventtime">Time: </label>
              </div>
              <div class="col-75">
                <input
                  type="text"
                  name="eventtime"
                  placeholder="02:00PM"
                  required
                  onChange={this.onTextChangeHandler}
                />
              </div>
            </div>

            <div class="row">
              <div class="col-25">
                <label for="location">Location: </label>
              </div>
              <div class="col-75">
                <input
                  type="text"
                  name="location"
                  placeholder="SanJoseStateUniversity - Student Union Ballroom"
                  required
                  onChange={this.onTextChangeHandler}
                />
              </div>
            </div>

            <div class="row">
              <div class="col-25">
                <label for="eligibility">Eligibility: </label>
              </div>
              <div class="col-75">
                <input
                  type="text"
                  name="eligibility"
                  placeholder="ALL/SpecificMajor"
                  required
                  onChange={this.onTextChangeHandler}
                />
              </div>
            </div>

            <div class="row">
              <input
                type="submit"
                value="Submit"
                onClick={this.onSubmitHandler}
              />
            </div>
          </form>
          <div class="row">
            <Link className="cancel" to="../Pages/CompEvents">
              Cancel
            </Link>
          </div>
        </div>
      </div>
    );
  }
}

export default AddEventForm;
