import React from "react";
import Axios from "axios";

import "./CompProfileForm.css";

class ViewCompProfileForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      companyname: "",
      location: "",
      phoneno: "",
      emailcontact: "",
      fax: "",
      website: "",
      description: "",
      companytype: "",
      authemail: ""
    };
    this.onSubmitHandler = this.onSubmitHandler.bind(this);
  }
  async onSubmitHandler(event) {
    event.preventDefault();
    let email = localStorage.getItem("email");
    await this.setState({ authemail: email });
    Axios.post(this.editorsave, this.state)
      .then(data => {
        console.log("data");
      })
      .catch(err => {
        console.log("error");
      });
  }
  render() {
    return (
      <div>
        <div class="container">
          <div class="row">
            <div class="col-25">Company Name :</div>
            <div class="col-75">{this.state.companyname || ""}</div>
          </div>
        </div>
      </div>
    );
  }
}

export default ViewCompProfileForm;
