import React from "react";
import FilterResults from "react-filter-search";
import Axios from "axios";

import Navbar from "../../Main/Pages/Navbar";
import "./StudentJobSearch.css";

class StudentJobSearch extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      jobs: [],
      searchTerm: ""
    };
    this.startfunc = this.startfunc.bind(this);
    this.searchTerm = this.searchTerm.bind(this);
  }

  searchTerm(e) {
    console.log(e.target.value);
    this.setState({ searchTerm: e.target.value });
  }

  startfunc() {
    Axios.get(`http://127.0.0.1:4001/studentviewalljobs`)
      // then((res) => res.json()).then((data) => {
      //   this.setState({users: data})
      // })}
      .then(data => {
        console.log(data.data);
        this.setState({ jobs: data.data });
      })
      .catch(err => {
        console.log(err);
      });
  }

  componentWillMount() {
    this.startfunc();
  }

  showDetails(job) {
    job.show = !job.show;
    this.setState({ ...job });
    console.log(this.state);
  }

  render() {
    return (
      <div>
        <Navbar />
        <div class="container">
          <h1> Jobs Posted </h1>
          <input
            class="search-box"
            onKeyUp={e => this.searchTerm(e)}
            type="text"
          ></input>

          <ul>
            {this.state.jobs
              .filter(job => {
                return (
                  job.companyname.toLowerCase().indexOf(this.state.searchTerm) >
                  -1
                );
              })
              .map(job => {
                return (
                  <li onClick={() => this.showDetails(job)}>
                    <h2>{job.companyname}</h2>
                    {job.show ? (
                      <div>
                        <p>{job.jobtitle}</p>
                        <p>{job.description}</p>
                        <p>{job.postingdate}</p>
                        <p>{job.applicationdate}</p>
                        <p>{job.location}</p>
                        <p>{job.email}</p>
                      </div>
                    ) : null}
                  </li>
                );
              })}
          </ul>
        </div>
      </div>
    );
  }
  //class
}

export default StudentJobSearch;
