import React from "react";
import Axios from "axios";

import "./CompProfileForm.css";

class compProfileForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = { location: "" };

    //this.editorsave = this.editorsave.bind(this);
    this.editorsave = "http://127.0.0.1:4001/saveCompProfile";

    this.onTextChangeHandler = this.onTextChangeHandler.bind(this);
    this.onSubmitHandler = this.onSubmitHandler.bind(this);
  }

  onTextChangeHandler(event) {
    this.setState({ [event.target.name]: event.target.value });
  }

  onSubmitHandler(event) {
    event.preventDefault();
    let email = localStorage.getItem("email");
    console.log(email);
    Axios.post(this.editorsave, email)
      .then(data => {
        console.log("data");
      })
      .catch(err => {
        console.log("error");
      });
  }

  render() {
    return (
      <div>
        compProfileForm
        <div class="container">
          <form>
            <div class="row">
              <div class="col-25">
                <label for="companyname">Company Name </label>
              </div>
              <div class="col-75">
                <input
                  type="text"
                  name="companyname"
                  value={this.state.name || ""}
                  onChange={this.onTextChangeHandler}
                />
              </div>
            </div>

            <div class="row">
              <div class="col-25">
                <label for="companyname">Company Name </label>
              </div>
              <div class="col-75">
                <input
                  type="text"
                  name="location"
                  value={this.state.name || ""}
                  onChange={this.onTextChangeHandler}
                />
              </div>
            </div>

            <div class="row">
              <div class="col-25">
                <label for="phoneno">Phone Number</label>
              </div>
              <div class="col-75">
                <input
                  type="text"
                  name="phoneno"
                  value={this.state.name || ""}
                  onChange={this.onTextChangeHandler}
                />
              </div>
            </div>

            <div class="row">
              <div class="col-25">
                <label for="phoneno">Phone Number</label>
              </div>
              <div class="col-75">
                <input
                  type="text"
                  name="emailcontact"
                  value={this.state.name || ""}
                  onChange={this.onTextChangeHandler}
                />
              </div>
            </div>

            <div class="row">
              <div class="col-25">
                <label for="phoneno">Phone Number</label>
              </div>
              <div class="col-75">
                <input
                  type="text"
                  name="fax"
                  value={this.state.name || ""}
                  onChange={this.onTextChangeHandler}
                />
              </div>
            </div>

            <div class="row">
              <div class="col-25">
                <label for="phoneno">Phone Number</label>
              </div>
              <div class="col-75">
                <input
                  type="text"
                  name="website"
                  value={this.state.name || ""}
                  onChange={this.onTextChangeHandler}
                />
              </div>
            </div>

            <div class="row">
              <div class="col-25">
                <label for="phoneno">Phone Number</label>
              </div>
              <div class="col-75">
                <textarea
                  name="description"
                  rows="1"
                  cols="100"
                  value={this.state.message || ""}
                  onChange={this.onTextChangeHandler}
                ></textarea>
              </div>
            </div>

            <div class="row">
              <div class="col-25">
                <label for="phoneno">Phone Number</label>
              </div>
              <div class="col-75">
                <input
                  type="text"
                  name="companytype"
                  value={this.state.name || ""}
                  onChange={this.onTextChangeHandler}
                />
              </div>
            </div>

            <div class="row">
              <input
                type="submit"
                value="Submit"
                onClick={this.onSubmitHandler}
              />
            </div>
          </form>
        </div>
      </div>
    );
  }
}

export default compProfileForm;
