import React from "react";
import { connect } from "react-redux";
import swal from "sweetalert";

import SigninAction from "../../Actions/SigninAction";

import TextFieldGroup from "./TextFieldGroup";
import validateSigninInput from "./validateSigninInput";

class Signinform extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      identifier: "",
      password: "",
      errors: {},
      isLoading: false,
      selectedOption: ""
    };

    this.onSubmit = this.onSubmit.bind(this);
    this.onChange = this.onChange.bind(this);
    //this.isValid = this.isValid.bind(this);
    //constructor
  }

  isValid() {
    let { errors, isValid } = validateSigninInput(this.state);
    if (!isValid) {
      //this.setState({ errors });
      this.setState(({ errors } = validateSigninInput(this.state)));
    }
    return isValid;
  }

  onSubmit(event) {
    event.preventDefault();
    if (this.isValid()) {
      this.setState({ errors: {}, isLoading: true });
      this.props
        .SigninAction(this.state)
        .then(swal("Sign-in Successful"))
        //then
        .catch(error => {
          console.log(error.response.data);
        });
      //catch

      //if
    }
    //function
  }

  onChange(event) {
    this.setState({ [event.target.name]: event.target.value });
  }

  render() {
    let { errors, identifier, password, isLoading } = this.state;
    return (
      <form onSubmit={this.onSubmit}>
        <h1> Sign-In </h1>

        <TextFieldGroup
          field="identifier"
          label="Username/Email"
          value={identifier}
          error={errors.identifier}
          onChange={this.onChange}
        />

        <TextFieldGroup
          field="password"
          label="Password"
          value={password}
          error={errors.password}
          onChange={this.onChange}
          type="password"
        />

        <div className="form-group">
          <button
            disabled={this.state.isLoading}
            type="submit"
            name="Submit"
            className="btn btn-primary btn-lg"
          >
            <b>Login</b>
          </button>
        </div>
        <div className="form-check">
          <input
            type="radio"
            id="company"
            name="loginuser"
            value="company"
            className="form-check-input"
            checked={this.state.selectedOption === "company"}
            onChange={this.handleOptionChange}
          />
          <label for="company">Company Login</label>
          <input
            type="radio"
            id="student"
            name="loginuser"
            value="student"
            className="form-check-input"
            checked={this.state.selectedOption === "student"}
            onChange={this.handleOptionChange}
          />
          <label for="student">Student Login</label>
          <br />
        </div>
      </form>

      //return
    );

    //render
  }

  //class
}

export default connect(null, { SigninAction })(Signinform);
