import React, { Component } from "react";
import axios from "axios";
// import cookie from "react-cookies";
// import { Redirect } from "react-router";
import { Link } from "react-router-dom";

class Signin extends Component {
  constructor(props) {
    super(props);

    this.state = {
      username: "",
      password: "",
      indicator: false
    };
    this.usernameChangeHandler = this.usernameChangeHandler.bind(this);
    this.passwordChangeHandler = this.passwordChangeHandler.bind(this);
    this.submitHandler = this.submitHandler.bind(this);

    //constructor
  }

  componentWillMount() {
    this.setState({
      indicator: false
    });
  }
  //All the change Handlers

  usernameChangeHandler = element => {
    this.setState({
      username: element.target.value
    });
  };

  passwordChangeHandler = element => {
    this.setState({
      password: element.target.value
    });
  };

  submitHandler = elemet => {
    //var headers = new Headers();
    elemet.preventDefault();
    const credentials = {
      username: this.state.username,
      password: this.state.password
    };

    //set the with credentials to true
    axios.defaults.withCredentials = true;

    //Post Request
    axios
      .post("http://localhost:4001/companysignin", credentials)
      .then(response => {
        console.log("Status code: ", response.status);
        if (response.status === 200) {
          this.setState({
            indicator: true
          });
        } else {
          this.setState({
            indicator: false
          });
        }

        //then
      })
      .catch(error => {
        alert("Invalid Credentials");
      });

    //submitHandler
  };

  render() {
    // let redirectVar = null;
    // if (cookie.load("cookie")) {
    //   redirectVar = <Redirect to="/home" />;
    // }
    return (
      <div class="form-group">
        <div class="usernameChangeHandler">
          <input
            onChange={this.usernameChangeHandler}
            type="text"
            class="form-control"
            name="username"
            placeholder="Username"
            className=".form-control"
          />
        </div>

        <div class="passwordChangeHandler">
          <input
            onChange={this.passwordChangeHandler}
            type="password"
            class="form-control"
            name="password"
            placeholder="Password"
            className=".form-control"
          />
        </div>

        <button onClick={this.submitHandler} class="btn btn-primary">
          Login
        </button>

        <Link
          className="Signupclick"
          to="../../Signup/Pages/Signup"
          class="btn btn-primary"
        >
          Signup
        </Link>
      </div>

      //return
    );
    //render
  }
  //class
}

// const Signin = () => {
//   return (
//     <div>
//       <h1>Signin</h1>
//     </div>
//   );
// };

export default Signin;
