import React from "react";
import Axios from "axios";
import swal from "sweetalert";

class StudentViewApplications extends React.Component {
  constructor(props) {
    super(props);
    this.state = { jobs: [] };

    this.startfunc = this.startfunc.bind(this);
  }

  async startfunc() {
    const email = localStorage.getItem("email");

    await Axios.post("http://127.0.0.1:4001/studentviewjobapplication", {
      email: email
    })
      .then(data => {
        if (data.data.status === "nostudent") {
          swal("", "No Jobs Applied");
        } else {
          this.setState({ jobs: data.data });
          console.log(this.state);
        }
      })
      .catch(err => console.log(err));
  }

  componentDidMount() {
    this.startfunc();
  }

  render() {
    const items = this.state.jobs
      .filter(job => {
        return (
          job.eventname
            .toLowerCase()
            .indexOf(this.state.searchTerm.toLowerCase()) > -1
        );
      })
      .map(item => (
        <li>
          <h2>{item.eventname}</h2>
          <h4>
            <b>{item.eventdate}</b> at <b>{item.eventtime}</b>
          </h4>
          <h4>Location : {item.location}</h4>
          <h3>
            Eligibility : <b>{item.eligibility}</b>
          </h3>
          <h2>Description</h2> {item.description}
          <button
            type="submit"
            id={item.eventid}
            name={item.eventid}
            value={item.eligibility}
            onClick={this.onClickHandler}
          >
            Register
          </button>
          <hr />
        </li>
      ));
    return (
      <div>
        <div className="container"></div>
      </div>
    );
  }
}

export default StudentViewApplications;
