import Axios from "axios";

function editstudentprofile(userData, type) {
  return dispatch => {
    if (type === "edit")
      return Axios.post("http://127.0.0.1:4001/getStudentProfile", userData);
    else if (type === "save")
      return Axios.post("http://127.0.0.1:4001/putStudentProfile", userData);
  };
}

function savestudentprofile(userData) {
  return dispatch => {
    return Axios.post("http://127.0.0.1:4001/putStudentProfile", userData);
  };
}

export default { editstudentprofile, savestudentprofile };
