import React, { Component } from "react";
import Axios from "axios";
import { connect } from "react-redux";
import reducer from "./../Store/reducer";

class CompanySignupForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      companyname: "",
      email: "",
      password: "",
      location: ""
    };
    this.CompanyNameChangeHandler = this.CompanyNameChangeHandler.bind(this);
    this.EmailChangeHandler = this.EmailChangeHandler.bind(this);
    this.PasswordChangeHandler = this.PasswordChangeHandler.bind(this);
    this.LocationChangeHandler = this.LocationChangeHandler.bind(this);
    this.SubmitHandler = this.SubmitHandler.bind(this);
    //constructor
  }

  CompanyNameChangeHandler(event) {
    this.setState({ [event.target.name]: event.target.value });
  }

  EmailChangeHandler(event) {
    this.setState({ [event.target.name]: event.target.value });
  }

  PasswordChangeHandler(event) {
    this.setState({ [event.target.name]: event.target.value });
  }

  LocationChangeHandler(event) {
    this.setState({ [event.target.name]: event.target.value });
  }

  SubmitHandler(event) {
    event.preventDefault();
    Axios.post("http://127.0.0.1/companysignup:4001", {
      user: this.state
    });
  }

  render() {
    return (
      <div className="form-group">
        <form onSubmit={this.SubmitHandler}>
          <div>
            Company Name:
            <input
              type="text"
              name="companyname"
              placeholder="Company Name"
              required
              className="form-control"
              value={this.props.companynameState}
              onChange={this.CompanyNameChangeHandler}
            />
          </div>

          <div>
            EMAIL:
            <input
              type="email"
              name="email"
              placeholder="EMAIL ID"
              required
              className="form-control"
              onChange={this.EmailChangeHandler}
            />
          </div>

          <div>
            Password:
            <input
              type="password"
              name="password"
              placeholder="Password"
              required
              className="form-control"
              onChange={this.PasswordChangeHandler}
            />
          </div>

          <div>
            Location:
            <input
              type="text"
              name="location"
              placeholder="Location"
              required
              className="form-control"
              onChange={this.LocationChangeHandler}
            />
          </div>
          <br />
          <div className="form-group">
            <button
              type="submit"
              name="Submit"
              className="btn btn-primary btn-lg"
            >
              <b>Create Account</b>
            </button>
          </div>
        </form>
      </div>
      //return
    );
    //render
  }
  //class
}

const mapStateToProps = state => {
  // you store instructions about how the state managed by redux should be mapped to props you can use in
  //in this container - Since props can be changed internally, we are mapping state to props...
  return {
    companynameState: state.companyname,
    emailstate: state.email,
    passwordstate: state.password,
    locationstate: state.location
  };
};

export default connect(mapStateToProps)(CompanySignupForm);
