import React, { Component } from "react";
import { Link } from "react-router-dom";
import cookie from "react-cookies";
//import { Redirect } from "react-router";
import {
  BrowserRouter as Router,
  Route,
  Redirect,
  Switch
} from "react-router-dom";
//files
import Navbar from "../../Pages/Navbar";
import BasicDetails from "../Components/View/BasicDetails";
import { connect } from "react-redux";

import viewProfileAction from "../../../Actions/viewProfileAction";
class StudentProfile extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      //onclick
      Bmessage: "",
      Bname: "",
      Bdob: "",
      Bcity: "",
      Bstate: "",
      Bcountry: "",
      Bemailid: "",
      Bphoneno: "",
      Bskillset: "",
      Bcollegename: "",
      Blocation: "",
      Bmajor: "",
      Bdegree: "",
      Byearofpassing: "",
      BCGPA: "",
      Bcompname: "",
      Btitle: "",
      Bcomplocation: "",
      Bstart: "",
      Bend: "",
      Bwork: "",

      //fromserver
      message: "",
      name: "",
      dob: "",
      city: "",
      state: "",
      country: "",
      emailid: "",
      phoneno: "",
      skillset: "",
      collegename: "",
      location: "",
      major: "",
      degree: "",
      yearofpassing: "",
      CGPA: "",
      compname: "",
      title: "",
      complocation: "",
      start: "",
      end: "",
      work: ""
    };
    this.showprofile = this.showprofile.bind(this);
    this.editProfileClickHandler = this.editProfileClickHandler.bind(this);
    this.BasicDetailsHandler = this.BasicDetailsHandler.bind(this);
    this.EduDetailsHandler = this.EduDetailsHandler.bind(this);
    this.compDetailHandler = this.compDetailHandler.bind(this);
  }

  editProfileClickHandler(event) {
    event.preventDefault();
    window.location = "./EditStudentProfile";
  }

  BasicDetailsHandler(event) {
    console.log("worked");
    event.preventDefault();
    let email = localStorage.getItem("email");
    this.props
      .viewProfileAction({ email: email })
      .then(data => {
        console.log("edit" + data.data.studentfullname);
        this.setState({
          collegename: data.data.schoolname,
          location: data.data.clglocation,
          degree: data.data.degree,
          major: data.data.major,
          yearofpassing: data.data.yearofpassing,
          CGPA: data.data.cgpa
        });

        showprofile = (
          <tr>
            <td>{this.state.name}</td>
            <td>{this.state.city}</td>
            <td>worked</td>
          </tr>
        );
      })

      .catch(err => console.log(err));
  }

  EduDetailsHandler(event) {
    console.log("two");
  }

  compDetailHandler(event) {
    console.log("three");
  }

  render() {
    return (
      <div>
        <Navbar />
        <h1> I am a profile</h1>
        <button
          type="button"
          class="btn btn-primary"
          onClick={this.editProfileClickHandler}
        >
          Edit Profile
        </button>
        <p>{localStorage.getItem("email")}</p>
        {/*
        <div className="container">
          <div className="row">
            <div className=".col-sm-12">
              <BasicDetails />
            </div>
          </div>
        </div>
        */}
        <div class="container">
          <h2>Profile</h2>
          <ul class="nav nav-tabs" role="tablist">
            <li class="active" onClick={this.BasicDetailsHandler}>
              <a href="#">BasicDetails</a>
            </li>
            <li>
              <a href="#" onClick={this.EduDetailsHandler}>
                Education Details
              </a>
            </li>
            <li>
              <a href="#" onClick={this.compDetailHandler}>
                Experience Details
              </a>
            </li>
          </ul>
          {this.showprofile}
        </div>
      </div>
    );
  }
}

export default connect(null, { viewProfileAction })(StudentProfile);
