import React from "react";
import { connect } from "react-redux";
import swal from "sweetalert";
import axios from "axios";
import SigninAction from "../../Actions/SigninAction";

import TextFieldGroup from "./TextFieldGroup";
import validateSigninInput from "./validateSigninInput";

class Signinform extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      identifier: "",
      password: "",
      errors: {},
      isLoading: false,
      selectedOption: "company",
      authFlag: false
    };

    this.onSubmit = this.onSubmit.bind(this);
    this.onChange = this.onChange.bind(this);
    this.handleOptionChange = this.handleOptionChange.bind(this);
    //constructor
  }

  //Call the Will Mount to set the auth Flag to false
  componentWillMount() {
    this.setState({
      authFlag: false
    });
  }

  isValid() {
    let { errors, isValid } = validateSigninInput(this.state);
    if (!isValid) {
      //this.setState({ errors });
      this.setState(({ errors } = validateSigninInput(this.state)));
    }
    return isValid;
  }

  onSubmit(event) {
    var headers = new Headers();
    event.preventDefault();
    if (this.isValid()) {
      axios.defaults.withCredentials = true;
      this.setState({ errors: {}, isLoading: true });
      this.props
        .SigninAction(this.state)
        .then(data => {
          console.log(data.data);
          this.setState({
            authFlag: true
          });
          swal("", "Sign-in Successful", "success").then(() => {
            window.location = "../../StudentDashboard/Pages/navbar";
          });
        })
        //then
        .catch(error => {
          console.log(error.response.data);
          if (error.response.data.problem === "password") {
            this.setState({
              authFlag: false
            });
            swal("Error", "Wrong Password!!!Try again", "error").then(() => {
              window.location = "/Signin/Pages/Signin";
            });
          } else if (error.response.data.problem === "username") {
            this.setState({
              authFlag: false
            });
            swal("Error", "Username is incorrect!!! Try again", "error").then(
              () => {
                window.location = "/Signin/Pages/Signin";
              }
            );
          } else {
            this.setState({
              authFlag: false
            });
            swal(
              "Error",
              "Some Error Occured.. Please try again",
              "error"
            ).then(() => {
              window.location = "/Signin/Pages/Signin";
            });
          }
        });
      //catch

      //if
    }
    //function
  }

  onChange(event) {
    this.setState({ [event.target.name]: event.target.value });
  }

  handleOptionChange = changeEvent => {
    this.setState({
      selectedOption: changeEvent.target.value
    });
  };

  render() {
    let { errors, identifier, password, isLoading } = this.state;
    return (
      <form onSubmit={this.onSubmit}>
        <h1> Sign-In </h1>

        <TextFieldGroup
          field="identifier"
          label="Username/Email"
          value={identifier}
          error={errors.identifier}
          onChange={this.onChange}
        />

        <TextFieldGroup
          field="password"
          label="Password"
          value={password}
          error={errors.password}
          onChange={this.onChange}
          type="password"
        />

        <div className="form-group">
          <button
            disabled={this.state.isLoading}
            type="submit"
            name="Submit"
            className="btn btn-primary btn-lg"
          >
            <b>Login</b>
          </button>
        </div>
        <div className="form-check">
          <input
            type="radio"
            id="company"
            name="loginuser"
            value="company"
            className="form-check-input"
            checked={this.state.selectedOption === "company"}
            onChange={this.handleOptionChange}
          />
          <label for="company">Company Login</label>
          <input
            type="radio"
            id="student"
            name="loginuser"
            value="student"
            className="form-check-input"
            checked={this.state.selectedOption === "student"}
            onChange={this.handleOptionChange}
          />
          <label for="student">Student Login</label>
          <br />
        </div>
      </form>

      //return
    );

    //render
  }

  //class
}

export default connect(null, { SigninAction })(Signinform);
