import React from "react";
import Axios from "axios";
import swal from "sweetalert";

class StudentViewApplications extends React.Component {
  constructor(props) {
    super(props);
    this.state = { jobs: [] };

    this.startfunc = this.startfunc.bind(this);
  }

  async startfunc() {
    const email = localStorage.getItem("email");

    await Axios.post("http://127.0.0.1:4001/studentviewjobapplication", {
      email: email
    })
      .then(data => {
        if (data.data.status === "nostudent") {
          swal("", "No Jobs Applied");
        } else {
          this.setState({ jobs: data.data });
          console.log(this.state);
        }
      })
      .catch(err => console.log(err));
  }

  componentDidMount() {
    this.startfunc();
  }

  render() {
    const items = this.state.jobs
      .filter(job => {
        if (job.applicationstatus === null) {
          job.applicationstatus = "undefined";

          return (
            job.applicationstatus
              .toLowerCase()
              .indexOf(this.state.searchTerm.toLowerCase()) > -1
          );
        }
      })
      .map(item => (
        <li>
          <h2>{item.jobtitle}</h2>
          <h4>
            <b>{item.postingdate}</b> till<b>{item.applicationdeadline}</b>
          </h4>
          <h4>Current Status : {item.applicationstatus}</h4>
          <button
            type="submit"
            id={item.jobid}
            name={item.jobid}
            onClick={this.onClickHandler}
          >
            Register
          </button>
          <hr />
        </li>
      ));
    return (
      <div>
        <div className="container"></div>
      </div>
    );
  }
}

export default StudentViewApplications;
