import React from "react";
import FilterResults from "react-filter-search";
import Axios from "axios";

import Navbar from "../../Main/Pages/Navbar";
import "./StudentJobSearch.css";

class StudentJobSearch extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      jobs: [],
      searchTerm: ""
    };
    this.startfunc = this.startfunc.bind(this);
    this.searchTerm = this.searchTerm.bind(this);
    this.showDetails = this.showDetails.bind(this);
    this.registerClickHandler = this.registerClickHandler.bind(this);
  }

  searchTerm(e) {
    console.log(e.target.value);
    this.setState({ searchTerm: e.target.value });
  }

  startfunc() {
    Axios.get(`http://127.0.0.1:4001/studentviewalljobs`)
      // then((res) => res.json()).then((data) => {
      //   this.setState({users: data})
      // })}
      .then(data => {
        console.log(data.data);
        this.setState({ jobs: data.data });
      })
      .catch(err => {
        console.log(err);
      });
  }

  componentWillMount() {
    this.startfunc();
  }

  showDetails(job) {
    job.show = !job.show;
    this.setState({ ...job });
    console.log(this.state);
  }

  registerClickHandler(event) {
    event.preventDefault();
    console.log("clicked");
  }

  render() {
    return (
      <div>
        <Navbar />
        <div class="container">
          <h1> Jobs Posted </h1>
          <input
            class="search-box"
            onKeyUp={e => this.searchTerm(e)}
            type="text"
          ></input>

          <ul className="collapse-able">
            {this.state.jobs
              .filter(job => {
                return (
                  job.companyname
                    .toLowerCase()
                    .indexOf(this.state.searchTerm.toLowerCase()) > -1 ||
                  job.jobtitle
                    .toLowerCase()
                    .indexOf(this.state.searchTerm.toLowerCase()) > -1
                );
              })
              .map(job => {
                return (
                  <li onClick={() => this.showDetails(job)}>
                    <h2>{job.companyname}</h2>
                    {job.show ? (
                      <div>
                        <p>
                          <b>{job.jobtitle}</b>
                        </p>
                        <p>{job.jobdescription}</p>
                        <p>Posted on{job.postingdate}</p>
                        <p>Apply Before : {job.applicationdeadline}</p>
                        <p>Job Location : {job.location}</p>
                        <p>Email : {job.companyemail}</p>
                        <button onClick={this.registerClickHandler}>
                          Register
                        </button>
                      </div>
                    ) : null}
                  </li>
                );
              })}
          </ul>
        </div>
      </div>
    );
  }
  //class
}

export default StudentJobSearch;
