import React, { Component } from "react";
import { Link } from "react-router-dom";
import cookie from "react-cookies";
//import { Redirect } from "react-router";
import { connect } from "react-redux";
import {
  BrowserRouter as Router,
  Route,
  Redirect,
  Switch
} from "react-router-dom";
import axios from "axios";

import educationeditsave from "../../../../Actions/educationeditsave";

class EditEducationDetails extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      collegename: "",
      location: "",
      major: "",
      degree: "",
      yearofpassing: "",
      CGPA: "",
      email: localStorage.getItem("email")
    };

    this.collegenameChangeHandler = this.collegenameChangeHandler.bind(this);
    this.majorChangeHandler = this.majorChangeHandler.bind(this);
    this.locationChangeHandler = this.locationChangeHandler.bind(this);
    this.degreeChangeHandler = this.degreeChangeHandler.bind(this);
    this.yopChangeHandler = this.yopChangeHandler.bind(this);
    this.CGPAChangeHandler = this.CGPAChangeHandler.bind(this);

    this.submitHandler = this.submitHandler.bind(this);
    this.editHandler = this.editHandler.bind(this);
  }

  collegenameChangeHandler(event) {
    this.setState({ [event.target.name]: event.target.value });
  }

  locationChangeHandler(event) {
    this.setState({ [event.target.name]: event.target.value });
  }

  degreeChangeHandler(event) {
    this.setState({ [event.target.name]: event.target.value });
  }

  majorChangeHandler(event) {
    this.setState({ [event.target.name]: event.target.value });
  }

  yopChangeHandler(event) {
    this.setState({ [event.target.name]: event.target.value });
  }

  CGPAChangeHandler(event) {
    this.setState({ [event.target.name]: event.target.value });
  }

  submitHandler(event) {
    event.preventDefault();
    let email = localStorage.getItem("email");
    let type;
    this.props
      .educationeditsave({ type: "save", data: this.state })
      .then(data => console.log(data))
      .catch(err => console.log(err));
  }

  editHandler(event) {
    event.preventDefault();
    let email = localStorage.getItem("email");
    let type;
    this.props
      .educationeditsave({ type: "edit", email: email })
      .then(data => {
        console.log("edit" + data.data.studentfullname);
        this.setState({
          collegename: data.data.schoolname,
          location: data.data.clglocation,
          degree: data.data.degree,
          major: data.data.major,
          yearofpassing: data.data.yearofpassing,
          CGPA: data.data.cgpa
        });
      })

      .catch(err => console.log(err));
  }

  render() {
    console.log(localStorage.getItem("email"));
    return (
      <div>
        <form onSubmit={this.submitHandler} className="form-group">
          College Name:
          <input
            type="text"
            name="collegename"
            className="form-control"
            value={this.state.collegename || ""}
            placeholder="College Name"
            onChange={this.collegenameChangeHandler}
          />
          Location:
          <input
            type="text"
            name="location"
            placeholder="Location"
            //required
            className="form-control"
            value={this.state.location || ""}
            onChange={this.locationChangeHandler}
          />
          Degree:
          <input
            type="date"
            name="degree"
            className="form-control"
            value={this.state.degree || ""}
            onChange={this.degreeChangeHandler}
          />
          Major:
          {/*  <input
            type="text"
            name="major"
            className="form-control"
            placeholder="Computer Engineering"
            value={this.state.major || ""}
            onChange={this.majorChangeHandler}
        /> */}
          <select
            name="major"
            onChange={this.majorChangeHandler}
            className="form-control"
            value={this.state.major || ""}
          >
            <option value="Computer Engineering">Computer Engineering</option>
            <option value="Software Engineering">Software Engineering</option>
            <option value="Electrical Engineering">
              Electrical Engineering
            </option>
            <option value="Mechanical Engineering">
              Mechanical Engineering
            </option>
          </select>
          Year Of Passing:
          <input
            type="text"
            name="yearofpassing"
            className="form-control"
            placeholder="California"
            value={this.state.yearofpassing || ""}
            onChange={this.yopChangeHandler}
          />
          current CGPA:
          <div className="form-group">
            <input
              type="text"
              name="CGPA"
              className="form-control"
              placeholder="4.0"
              value={this.state.CGPA || ""}
              onChange={this.CGPAChangeHandler}
            />
          </div>
          <div className="form-group">
            <button
              type="submit"
              name="Submit"
              className="btn btn-primary btn-lg"
            >
              <b>Save</b>
            </button>
          </div>
          <div className="form-group">
            <button
              type="submit"
              name="edit"
              className="btn btn-primary btn-lg"
              onClick={this.editHandler}
            >
              <b>Show saved data</b>
            </button>
          </div>
        </form>
      </div>
    );
  }
}

export default connect(null, { educationeditsave })(EditEducationDetails);
