import React from "react";
import FilterResults from "react-filter-search";
import Axios from "axios";

import Navbar from "../../Main/Pages/Navbar";
import "./StudentJobSearch.css";

class StudentJobSearch extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      jobs: [],
      searchTerm: "",
      redirector: null
    };
    this.startfunc = this.startfunc.bind(this);
    this.searchTerm = this.searchTerm.bind(this);
    this.showDetails = this.showDetails.bind(this);
    this.registerClickHandler = this.registerClickHandler.bind(this);
  }

  searchTerm(e) {
    console.log(e.target.value);
    this.setState({ searchTerm: e.target.value });
  }

  startfunc() {
    Axios.get(`http://127.0.0.1:4001/studentviewalljobs`)
      // then((res) => res.json()).then((data) => {
      //   this.setState({users: data})
      // })}
      .then(data => {
        console.log(data.data);
        this.setState({ jobs: data.data });
      })
      .catch(err => {
        console.log(err);
      });
  }

  componentWillMount() {
    this.startfunc();
  }

  showDetails(job) {
    job.show = !job.show;
    this.setState({ ...job });
  }

  registerClickHandler(event) {
    event.preventDefault();
    //console.log("event.target.value");
    var jobid = event.target.value;
    this.setState({
      redirector: (
        <Redirect
          to={{
            pathname:
              "/StudentDashboard/Main/StudentJobSearch/Components/StudentViewandRegister",
            state: { jobs: this.state.jobs, jobid: jobid }
          }}
        />
      )
    });
  }

  render() {
    return (
      <div>
        {this.state.redirector}
        <Navbar />
        <div class="container">
          <h1> Jobs Posted </h1>
          <input
            class="search-box"
            onKeyUp={e => this.searchTerm(e)}
            type="text"
          ></input>
          <div className="h22">
            <ul className="collapse-able">
              {this.state.jobs
                .filter(job => {
                  return (
                    job.companyname
                      .toLowerCase()
                      .indexOf(this.state.searchTerm.toLowerCase()) > -1 ||
                    job.jobtitle
                      .toLowerCase()
                      .indexOf(this.state.searchTerm.toLowerCase()) > -1
                  );
                })
                .map(job => {
                  return (
                    <li
                      className="liclass"
                      onClick={() => this.showDetails(job)}
                    >
                      <h3>{job.companyname}</h3>
                      <p>
                        <b>{job.jobtitle}</b>
                      </p>
                      {job.show ? (
                        <div>
                          <p>Apply Before : {job.applicationdeadline}</p>

                          <button
                            onClick={this.registerClickHandler}
                            value={job.jobid}
                          >
                            ViewDetails
                          </button>
                        </div>
                      ) : null}
                    </li>
                  );
                })}
            </ul>
          </div>
        </div>
      </div>
    );
  }
  //class
}

export default StudentJobSearch;
