import React, { Component } from "react";
import { Link } from "react-router-dom";
import cookie from "react-cookies";
import {
  BrowserRouter as Router,
  Route,
  Redirect,
  Switch
} from "react-router-dom";
import "./Navbar.css";

class Navbar extends React.Component {
  constructor(props) {
    super(props);
    this.handleLogout = this.handleLogout.bind(this);
    this.state = { activator: "JobSearch" };
  }

  //handle logout to destroy the cookie
  handleLogout = () => {
    cookie.remove("cookie", { path: "/" });
    localStorage.removeItem("email");
  };

  componentDidMount() {
    var mainContainer = document.getElementById("topnavid");
    var btns = mainContainer.getElementsByClassName("btn");
    for (var i = 0; i < btns.length; i++) {
      btns[i].addEventListener("click", function() {
        var current = document.getElementsByClassName("active");

        // If there's no active class
        if (current.length > 0) {
          current[0].className = current[0].className.replace(" active", "");
        }

        // Add the active class to the current/clicked button
        this.className += " active";
      });
    }
  }

  render() {
    let navLogin = null;
    let redirectVar = null;
    if (cookie.load("cookie")) {
      console.log("Able to read cookie");
      navLogin = (
        <ul class="nav navbar-nav navbar-right">
          <li>
            <Link to="/" onClick={this.handleLogout}>
              <span class="glyphicon glyphicon-user"></span>Logout
            </Link>
          </li>
        </ul>
      );
    } else {
      console.log("NOt Able to read cookie");
      redirectVar = <Redirect to="/" />;
    }

    return (
      <div class="background">
        <div class="topnav" id="topnavid">
          {redirectVar}
          <a className="btn" href="../../StudentProfiles/Pages/StudentProfile">
            Profile
          </a>
          <a
            className="btn"
            href="../../StudentJobSearch/Pages/StudentJobSearch"
          >
            JobSearch
          </a>
          <a className="btn" href="#">
            Applications
          </a>

          <a
            className="btn"
            href="../../StudentEvents/Pages/StudentViewAllEvents"
          >
            Events
          </a>

          <a className="btn" href="#">
            Students
          </a>

          {navLogin}
        </div>
      </div>

      //return
    );
    //render
  }
  //class
}

export default Navbar;
