import React from "react";

class AddEventForm extends React.Component {
  constructor(props) {
    super(props);
  }
  render() {
    return (
      <div>
        <div class="container">
          <form>
            <div class="row">
              <div class="col-25">
                <label for="eventname">Event Name : </label>
              </div>
              <div class="col-75">
                <input
                  type="text"
                  name="eventname"
                  value={this.state.eventname || ""}
                  onChange={this.onTextChangeHandler}
                />
              </div>
            </div>

            <div class="row">
              <div class="col-25">
                <label for="time">Time: </label>
              </div>
              <div class="col-75">
                <input
                  type="text"
                  name="time"
                  value={this.state.time || ""}
                  onChange={this.onTextChangeHandler}
                />
              </div>
            </div>

            <div class="row">
              <div class="col-25">
                <label for="date">Date: </label>
              </div>
              <div class="col-75">
                <input
                  type="text"
                  name="date"
                  value={this.state.date || ""}
                  onChange={this.onTextChangeHandler}
                />
              </div>
            </div>
          </form>
        </div>
      </div>
    );
  }
}

export default AddEventForm;
