import React from "react";
import Axios from "axios";

import "./CompProfileForm.css";
import { Redirect } from "react-router";

class compProfileForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      companyname: "",
      location: "",
      phoneno: "",
      emailcontact: "",
      fax: "",
      website: "",
      description: "",
      companytype: "",
      authemail: ""
    };
    this.redirector = false;
    //this.editorsave = this.editorsave.bind(this);
    this.editorsave = "http://127.0.0.1:4001/saveCompProfile";

    this.onTextChangeHandler = this.onTextChangeHandler.bind(this);
    this.onSubmitHandler = this.onSubmitHandler.bind(this);
  }

  onTextChangeHandler(event) {
    this.setState({ [event.target.name]: event.target.value });
  }

  async onSubmitHandler(event) {
    event.preventDefault();
    let email = localStorage.getItem("email");
    await this.setState({ authemail: email });
    Axios.post(this.editorsave, this.state)
      .then(data => {
        console.log("data");
      })
      .catch(err => {
        console.log("error");
      });
    this.redirector = true;
  }

  render() {
    let redirection = null;
    if (redirector == true) {
      redirection = (
        <Redirect to="/CompanyDashboard/CompProfile/Components/ViewCompProfileForm" />
      );
    }
    return (
      <div>
        {redirection}
        compProfileForm
        <div class="container">
          <form>
            <div class="row">
              <div class="col-25">
                <label for="companyname">Company Name : </label>
              </div>
              <div class="col-75">
                <input
                  type="text"
                  name="companyname"
                  value={this.state.companyname || ""}
                  onChange={this.onTextChangeHandler}
                />
              </div>
            </div>

            <div class="row">
              <div class="col-25">
                <label for="location">Location : </label>
              </div>
              <div class="col-75">
                <input
                  type="text"
                  name="location"
                  value={this.state.location || ""}
                  onChange={this.onTextChangeHandler}
                />
              </div>
            </div>

            <div class="row">
              <div class="col-25">
                <label for="phoneno">Phone Number : </label>
              </div>
              <div class="col-75">
                <input
                  type="text"
                  name="phoneno"
                  value={this.state.phoneno || ""}
                  onChange={this.onTextChangeHandler}
                />
              </div>
            </div>

            <div class="row">
              <div class="col-25">
                <label for="emailcontact">E-Mail : </label>
              </div>
              <div class="col-75">
                <input
                  type="text"
                  name="emailcontact"
                  value={this.state.emailcontact || ""}
                  onChange={this.onTextChangeHandler}
                />
              </div>
            </div>

            <div class="row">
              <div class="col-25">
                <label for="fax">Fax Number : </label>
              </div>
              <div class="col-75">
                <input
                  type="text"
                  name="fax"
                  value={this.state.fax || ""}
                  onChange={this.onTextChangeHandler}
                />
              </div>
            </div>

            <div class="row">
              <div class="col-25">
                <label for="website">Website : </label>
              </div>
              <div class="col-75">
                <input
                  type="text"
                  name="website"
                  value={this.state.website || ""}
                  onChange={this.onTextChangeHandler}
                />
              </div>
            </div>

            <div class="row">
              <div class="col-25">
                <label for="description">Description : </label>
              </div>
              <div class="col-75">
                <textarea
                  name="description"
                  rows="1"
                  cols="100"
                  value={this.state.description || ""}
                  onChange={this.onTextChangeHandler}
                ></textarea>
              </div>
            </div>

            <div class="row">
              <div class="col-25">
                <label for="companytype">Company Type : </label>
              </div>
              <div class="col-75">
                <input
                  type="text"
                  name="companytype"
                  value={this.state.companytype || ""}
                  onChange={this.onTextChangeHandler}
                />
              </div>
            </div>

            <div class="row">
              <input
                type="submit"
                value="Submit"
                onClick={this.onSubmitHandler}
              />
            </div>
          </form>
        </div>
      </div>
    );
  }
}

export default compProfileForm;
