import React, { Component } from "react";
import axios from "axios";
import { connect } from "react-redux";
import PropTypes from "prop-types";
//import reducer from "../../Store/reducer";

class CompanySignupForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      companyname: "",
      email: "",
      password: "",
      location: ""
    };
    this.companyNameChangeHandler = this.companyNameChangeHandler.bind(this);
    this.emailChangeHandler = this.emailChangeHandler.bind(this);
    this.passwordChangeHandler = this.passwordChangeHandler.bind(this);
    this.locationChangeHandler = this.locationChangeHandler.bind(this);
    this.submitHandler = this.submitHandler.bind(this);
    //constructor
  }

  companyNameChangeHandler(event) {
    this.setState({ [event.target.name]: event.target.value });
  }

  emailChangeHandler(event) {
    this.setState({ [event.target.name]: event.target.value });
  }

  passwordChangeHandler(event) {
    this.setState({ [event.target.name]: event.target.value });
  }

  locationChangeHandler(event) {
    this.setState({ [event.target.name]: event.target.value });
  }

  submitHandler(event) {
    event.preventDefault();
    this.props.companySignupRequest(this.state).then(
      () => {},
      ({ data }) => this.setState({ errors: data })
    );
    // axios.post("http://127.0.0.1:4001/companysignup", {
    //   company: this.state
    // });
  }

  render() {
    return (
      <div className="form-group">
        <form onSubmit={this.submitHandler}>
          <div>
            Company Name:
            <input
              type="text"
              name="companyname"
              placeholder="Company Name"
              required
              className="form-control"
              //value={this.props.companynameState}
              onChange={this.companyNameChangeHandler}
            />
          </div>

          <div>
            EMAIL:
            <input
              type="email"
              name="email"
              placeholder="EMAIL ID"
              required
              className="form-control"
              ///value={this.props.emailState}
              onChange={this.emailChangeHandler}
            />
          </div>

          <div>
            Password:
            <input
              type="password"
              name="password"
              placeholder="Password"
              required
              className="form-control"
              //value={this.props.passwordState}
              onChange={this.passwordChangeHandler}
            />
          </div>

          <div>
            Location:
            <input
              type="text"
              name="location"
              placeholder="Location"
              required
              className="form-control"
              //value={this.props.locationState}
              onChange={this.locationChangeHandler}
            />
          </div>
          <br />
          <div className="form-group">
            <button
              type="submit"
              name="Submit"
              className="btn btn-primary btn-lg"
            >
              <b>Create Account</b>
            </button>
          </div>
        </form>
      </div>
      //return
    );
    //render
  }
  //class
}

