import React, { Component } from "react";
import { Link } from "react-router-dom";
import cookie from "react-cookies";
//import { Redirect } from "react-router";
import { connect } from "react-redux";
import {
  BrowserRouter as Router,
  Route,
  Redirect,
  Switch
} from "react-router-dom";
import axios from "axios";

import companyeditsave from "../../../../Actions/companyeditsave";

class EditCompanyDetails extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      compname: "",
      title: "",
      complocation: "",
      start: "",
      end: "",
      work: ""
    };

    this.compnameChangeHandler = this.compnameChangeHandler.bind(this);
    this.titleChangeHandler = this.titleChangeHandler.bind(this);
    this.workChangeHandler = this.workChangeHandler.bind(this);
    this.complocationChangeHandler = this.complocationChangeHandler.bind(this);
    this.startChangeHandler = this.startChangeHandler.bind(this);
    this.endChangeHandler = this.endChangeHandler.bind(this);

    this.submitHandler = this.submitHandler.bind(this);
    this.editHandler = this.editHandler.bind(this);
  }

  compnameChangeHandler(event) {
    this.setState({ [event.target.name]: event.target.value });
  }

  titleChangeHandler(event) {
    this.setState({ [event.target.name]: event.target.value });
  }

  workChangeHandler(event) {
    this.setState({ [event.target.name]: event.target.value });
  }

  complocationChangeHandler(event) {
    this.setState({ [event.target.name]: event.target.value });
  }

  startChangeHandler(event) {
    this.setState({ [event.target.name]: event.target.value });
  }

  endChangeHandler(event) {
    this.setState({ [event.target.name]: event.target.value });
  }

  submitHandler(event) {
    event.preventDefault();
    let email = localStorage.getItem("email");
    let type;
    this.props
      .companyeditsave({ type: "save", data: this.state })
      .then(data => console.log(data))
      .catch(err => console.log(err));
  }

  editHandler(event) {
    event.preventDefault();
    let email = localStorage.getItem("email");
    let type;
    this.props
      .companyeditsave({ type: "edit", email: email })
      .then(data => {
        console.log("edit" + data.data.studentfullname);
        this.setState({
          compname: data.data.company,
          title: data.data.title,
          complocation: data.data.startdate,
          start: data.data.enddate,
          end: data.data.enddate,
          work: data.data.workdescription
        });
        // .catch(err => {
        //   console.log(err);
      })
      // })
      .catch(err => console.log(err));
  }

  render() {
    console.log(localStorage.getItem("email"));
    return (
      <div>
        <form onSubmit={this.submitHandler} className="form-group">
          Career Objective:
          <textarea
            name="work"
            rows="1"
            cols="100"
            className="form-control"
            value={this.state.work || ""}
            onChange={this.workChangeHandler}
          >
            Work
          </textarea>
          <br />
          Company Name:
          <input
            type="text"
            name="compname"
            placeholder="Company Name"
            //required
            className="form-control"
            value={this.state.compname || ""}
            onChange={this.compnameChangeHandler}
          />
          Start Date:
          <input
            type="date"
            name="start"
            className="form-control"
            value={this.state.start || ""}
            onChange={this.startChangeHandler}
          />
          End Date:
          <input
            type="date"
            name="end"
            className="form-control"
            value={this.state.end || ""}
            onChange={this.endChangeHandler}
          />
          City:
          <input
            type="text"
            name="title"
            className="form-control"
            placeholder="Manager"
            value={this.state.title || ""}
            onChange={this.titleChangeHandler}
          />
          State:
          <input
            type="text"
            name="complocation"
            className="form-control"
            placeholder="California"
            value={this.state.complocation || ""}
            onChange={this.complocationChangeHandler}
          />
          <div className="form-group">
            <button
              type="submit"
              name="Submit"
              className="btn btn-primary btn-lg"
            >
              <b>Save</b>
            </button>
          </div>
          <div className="form-group">
            <button
              type="submit"
              name="edit"
              className="btn btn-primary btn-lg"
              onClick={this.editHandler}
            >
              <b>Show saved data</b>
            </button>
          </div>
        </form>
      </div>
    );
  }
}

export default connect(null, { companyeditsave })(EditCompanyDetails);
