import React from "react";
import Axios from "axios";

class compProfileForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = { location: "" };

    //this.editorsave = this.editorsave.bind(this);
    this.editorsave = "http://127.0.0.1:4001/saveCompProfile";

    this.onTextChangeHandler = this.onTextChangeHandler.bind(this);
    this.onSubmitHandler = this.onSubmitHandler.bind(this);
  }

  onTextChangeHandler(event) {
    this.setState({ [event.target.name]: event.target.value });
  }

  onSubmitHandler(event) {
    event.preventDefault();
    let email = localStorage.getItem("email");
    console.log(email);
    Axios.post(this.editorsave, email)
      .then(data => {
        console.log("data");
      })
      .catch(err => {
        console.log("error");
      });
  }

  render() {
    return (
      <div>
        compProfileForm
        <div>
          <form>
            <span>Company Name : </span>
            <div>
              <input
                type="text"
                name="companyname"
                value={this.state.name || ""}
                onChange={this.onTextChangeHandler}
              />
            </div>

            <div>
              <input
                type="text"
                name="location"
                value={this.state.name || ""}
                onChange={this.onTextChangeHandler}
              />
            </div>

            <div>
              <input
                type="text"
                name="phoneno"
                value={this.state.name || ""}
                onChange={this.onTextChangeHandler}
              />
            </div>

            <div>
              <input
                type="text"
                name="emailcontact"
                value={this.state.name || ""}
                onChange={this.onTextChangeHandler}
              />
            </div>

            <div>
              <input
                type="text"
                name="fax"
                value={this.state.name || ""}
                onChange={this.onTextChangeHandler}
              />
            </div>

            <div>
              <input
                type="text"
                name="website"
                value={this.state.name || ""}
                onChange={this.onTextChangeHandler}
              />
            </div>

            <textarea
              name="description"
              rows="1"
              cols="100"
              value={this.state.message || ""}
              onChange={this.onTextChangeHandler}
            >
              Career Goals
            </textarea>

            <div>
              <input
                type="text"
                name="companytype"
                value={this.state.name || ""}
                onChange={this.onTextChangeHandler}
              />
            </div>

            <button type="submit" name="submit" onClick={this.onSubmitHandler}>
              Submit
            </button>
          </form>
        </div>
      </div>
    );
  }
}

export default compProfileForm;
