import React from "react";
import FilterResults from "react-filter-search";
import Axios from "axios";
import "./viewstudents.css";

//files

class ViewStudents extends React.Component {
  constructor(props) {
    super(props);
    this.state = { students: [], searchTerm: "nostudent" };

    this.startfunc = this.startfunc.bind(this);
    this.searchTerm = this.searchTerm.bind(this);
    this.showDetails = this.showDetails.bind(this);
  }

  searchTerm(event) {
    if ((event.target.value = "")) {
      this.setState({ searchTerm: "nostudent" });
    } else {
      this.setState({ searchTerm: event.target.value });
    }
  }

  async startfunc() {
    await Axios.get(`http://127.0.0.1:4001/viewallstudents`)
      .then(data => {
        console.log(data.data);
        this.setState({ students: data.data });
      })
      .catch(err => {
        console.log(err);
      });
  }

  componentDidMount() {
    this.startfunc();
  }

  showDetails(student) {
    student.show = !student.show;
    this.setState({ ...student });
  }

  render() {
    console.log(this.state.students);
    return (
      <div>
        <div class="container">
          <input
            placeholder="Search using JobTitle or Company Name"
            class="search-box"
            onKeyUp={e => this.searchTerm(e)}
            type="text"
          ></input>

          <div className="h22">
            <ul className="collapse-able">
              {this.state.students
                .filter(student => {
                  return (
                    student.studentfullname
                      .toLowerCase()
                      .indexOf(this.state.searchTerm.toLowerCase()) > -1 ||
                    student.schoolname
                      .toLowerCase()
                      .indexOf(this.state.searchTerm.toLowerCase()) > -1
                  );
                })
                .map(student => {
                  return (
                    <li
                      className="liclass"
                      onClick={() => this.showDetails(student)}
                    >
                      <h3>{student.studentfullname}</h3>
                      <p>
                        <b>{student.schoolname}</b>
                      </p>
                      {student.show ? (
                        <div>
                          <p> {student.skillset}</p>
                          <p> {student.location}</p>
                          <button
                            onClick={this.registerClickHandler}
                            value="12"
                          >
                            ViewDetails
                          </button>
                        </div>
                      ) : null}
                    </li>
                  );
                })}
            </ul>
          </div>
        </div>
      </div>
    );
  }
}

export default ViewStudents;
