import Axios from "axios";
import React, { Component } from "react";

class ViewEventForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      eventname: "",
      description: "",
      eventtime: "",
      eventdate: "",
      location: "",
      eligibility: ""
    };
    this.startfunc = this.startfunc.bind(this);
  }

  startfunc() {
    //event.preventDefault();
    let email = localStorage.getItem("email");
    Axios.post("http://127.0.0.1:4001/companyviewevent", { authemail: email })
      .then(data => {
        console.log(data);
        this.setState({
          eventname: data.data.companyname,
          description: data.data.description,
          eventtime: data.data.eventtime,
          eventdate: data.data.eventdate,
          location: data.data.location,
          eligibility: data.data.eligibility
        });
      })
      .catch(err => {
        console.log(err);
      });
  }

  componentWillMount() {
    this.startfunc();
  }

  render() {
    return (
      <div>
        <div className="Container">{this.state.eventname}</div>
      </div>
    );
  }
}
export default ViewEventForm;
