import React from "react";
import FilterResults from "react-filter-search";
import Axios from "axios";

//files

class ViewStudents extends React.Component {
  constructor(props) {
    super(props);
    this.state = { searchTerm: "", students: null };

    this.startfunc = this.startfunc.bind(this);
    this.searchTerm = this.searchTerm.bind(this);
    this.showDetails = this.showDetails.bind(this);
  }

  searchTerm(e) {
    this.setState({ searchTerm: e.target.value });
  }

  startfunc() {
    Axios.get(`http://127.0.0.1:4001/viewallstudents`)
      .then(data => {
        console.log(data.data);
        this.setState({ students: data.data });
      })
      .catch(err => {
        console.log(err);
      });
  }
  componentWillMount() {
    this.startfunc();
  }

  showDetails(job) {
    job.show = !job.show;
    this.setState({ ...job });
  }

  render() {
    return (
      <div>
        <div class="container">
          <input
            placeholder="Search using JobTitle or Company Name"
            class="search-box"
            onKeyUp={e => this.searchTerm(e)}
            type="text"
          ></input>
          <div className="h22">
            <ul className="collapse-able">
              {this.state.jobs
                .filter(job => {
                  return (
                    job.companyname
                      .toLowerCase()
                      .indexOf(this.state.searchTerm.toLowerCase()) > -1 ||
                    job.jobtitle
                      .toLowerCase()
                      .indexOf(this.state.searchTerm.toLowerCase()) > -1
                  );
                })
                .map(job => {
                  return (
                    <li
                      className="liclass"
                      onClick={() => this.showDetails(job)}
                    >
                      <h3>{job.companyname}</h3>
                      <p>
                        <b>{job.jobtitle}</b>
                      </p>
                      {job.show ? (
                        <div>
                          <p> {job.jobcategory}</p>
                          <p> {job.location}</p>
                          <button
                            onClick={this.registerClickHandler}
                            value={job.jobid}
                          >
                            ViewDetails
                          </button>
                        </div>
                      ) : null}
                    </li>
                  );
                })}
            </ul>
          </div>
        </div>
      </div>
    );
  }
}

export default ViewStudents;
