import React, { Component } from "react";
import Axios from "axios";
import { Redirect } from "react-router-dom";
import swal from "sweetalert";

class CompanyViewJobs extends Component {
  constructor(props) {
    super(props);
    this.state = { events: [], redirector: null, registeredstudents: "" };
    this.startfunc = this.startfunc.bind(this);
    // this.onClickHandler = this.onClickHandler.bind(this);
  }

  startfunc() {
    //event.preventDefault();
    let email = localStorage.getItem("email");
    Axios.post("http://127.0.0.1:4001/companyviewjobs", {
      companyemail: email
    })
      .then(data => {
        //console.log(data);
        this.setState({
          events: data.data
        });
        console.log(this.state.events);
      })
      .catch(err => {
        console.log(err);
      });
  }
  componentDidMount() {
    this.startfunc();
  }
  render() {
    const items = this.state.events.map(item => (
      <li>
        <h2>{item.jobtitle}</h2>
        <h5>{item.jobcategory}</h5>
        <h4>
          on {item.postingdate} till {item.applicationdeadline}
        </h4>
        <button
          type="details"
          name={item.jobid}
          onClick={this.onDetailsClickHandler}
        >
          Details
        </button>
        {"     "}
        <button
          type="submit"
          name={item.jobid}
          onClick={this.onViewStudentsClickHandler}
        >
          See List of Students
        </button>
        <hr />
      </li>
    ));

    return <div className="container">{items}</div>;
  }
}

export default CompanyViewJobs;
