import React from "react";
//files
import CompNavbar from "../../main/Pages/CompNavbar";
import CompProfileForm from "../Components/CompProfileForm";
class CompProfile extends React.Component {
  constructor(props) {
    super(props);
    this.editorview = "view";
    this.onclickhandler = this.onclickhandler.bind(this);
  }
  onclickhandler(event) {
    this.editorview = "edit";
  }
  render() {
    var editorviewrender = <ViewCompProfileForm />;
    if (this.editorview === "edit") {
      editorviewrender = <CompProfileForm />;
    }
    return (
      <div>
        <CompNavbar />
        {editorviewrender}
        <div>
          <button onClick="this.onclickhandler">Edit</button>
        </div>
      </div>
    );
  }
}

export default CompProfile;
