import React from "react";
import Axios from "axios";

class compProfileForm extends React.Component {
  constructor(props) {
    super(props);
    //this.editorsave = "http://127.0.0.1:4001/getStudentEduDetail";
    this.onSubmitHandler = this.onSubmitHandler.bind(this);
  }
  onSubmitHandler(event) {
    let email = localStorage.getItem("email");
    Axios.post("http://127.0.0.1:4001/studentsignup", email);
  }

  render() {
    return (
      <div>
        compProfileForm
        <div>
          <form>
            <input type="text" name="companyname" />
            <button type="submit" name="submit" onClick={this.onSubmitHandler}>
              Submit
            </button>
          </form>
        </div>
      </div>
    );
  }
}

export default compProfileForm;
