import React from "react";
import Axios from "axios";

import Navbar from "../../Main/Pages/Navbar";

class StudentViewAllEvents extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      events: []
    };
    this.startfunc = this.startfunc.bind(this);
    //this.togglePopup = this.togglePopup.bind(this);
    //this.onClickHandler = this.onClickHandler.bind(this);
  }

  startfunc() {
    //event.preventDefault();
    this.setState({
      showPopup: !this.state.showPopup
    });
    let email = localStorage.getItem("email");
    Axios.get("http://127.0.0.1:4001/studentviewevent")
      .then(data => {
        //console.log(data);
        this.setState({
          //   eventname: data.data.eventname,
          //   description: data.data.description,
          //   eventtime: data.data.eventtime,
          //   eventdate: data.data.eventdate,
          //   location: data.data.location,
          //   eligibility: data.data.eligibility
          events: data.data,
          showPopup: false
        });
        console.log(this.state.events);
      })
      .catch(err => {
        console.log(err);
      });
  }
  componentDidMount() {
    this.startfunc();
  }

  // togglePopup() {

  // }

  onClickHandler(event) {
    event.preventDefault();
    Axios.post("http://127.0.0.1:4001/studenteventdetail", {
      eventid: event.target.name
    })
      .then(data => {
        console.log(data.data);
        // this.setState({
        //   redirector: (
        //     <Redirect
        //       to={{
        //         pathname:
        //           "/CompanyDashboard/CompEvents/Pages/ViewRegisteredStudents",
        //         state: { registeredstudents: data.data }
        //       }}
        //     />
        //   )
        // });
      })
      .catch(err => {
        console.log(err);
      });
  }

  render() {
    const items = this.state.events.map(item => (
      <li>
        <h2>{item.eventname}</h2>
        <h4>{item.eventdate}</h4>
        <button type="submit" name={item.eventid} onClick={this.onClickHandler}>
          View Details
        </button>
        <hr />
      </li>
    ));
    return (
      <div>
        <Navbar />
        <div className="container">{items}</div>
      </div>
    );
  }
}

export default StudentViewAllEvents;
