import React from "react";
import { Link } from "react-router-dom";

class ViewJOBDetails extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      events: this.props.location.state.event,
      jobid: this.props.location.state.jobid
    };
    console.log(this.state.events[0]);
  }

  startfunc() {
    var items = this.state.events.map(item => {
      if (item.jobid == this.state.jobid) {
        console.log(item.jobtitle);
      }
    });
  }

  componentDidMount() {
    this.startfunc();
  }

  render() {
    return (
      <div>
        ViewJOBDetails
        <Link className="Signinclick" to="../Pages/CompDashboard">
          Back
        </Link>
        <div className="container">
          <h2> {this.state.events.jobtitle}</h2>
        </div>
        <div className="container">
          <div class="row">
            <div class="col-25">Job Description:</div>
            <div class="col-75">{this.state.events.jobdescription || ""}</div>
          </div>
          <div class="row">
            <div class="col-25">Job Posted On :</div>
            <div class="col-75">{this.state.events.postingdate || ""}</div>
          </div>
          <div class="row">
            <div class="col-25">Application Deadline :</div>
            <div class="col-75">
              {this.state.events.applicationdeadline || ""}
            </div>
          </div>
          <div class="row">
            <div class="col-25">Job Category :</div>
            <div class="col-75">{this.state.profile.jobcategory || ""}</div>
          </div>

          <div class="row">
            <div class="col-25">Location :</div>
            <div class="col-75">{this.state.events.location || ""}</div>
          </div>
          <div class="row">
            <div class="col-25">Salary Offered:</div>
            <div class="col-75">{this.state.events.salary || ""}</div>
          </div>
        </div>
      </div>
    );
  }
}

export default ViewJOBDetails;
