import React, { Component } from "react";
import { Link } from "react-router-dom";
import cookie from "react-cookies";
//import { Redirect } from "react-router";
import { connect } from "react-redux";
import {
  BrowserRouter as Router,
  Route,
  Redirect,
  Switch
} from "react-router-dom";
import axios from "axios";

import editstudentprofile from "../../../../Actions/BasicDetailseditsave";

class EditBasicDetails extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      message: "",
      name: "",
      dob: "",
      city: "",
      state: "",
      country: "",
      emailid: "",
      phoneno: "",
      skillset: ""
    };

    this.messageChangeHandler = this.messageChangeHandler.bind(this);
    this.nameChangeHandler = this.nameChangeHandler.bind(this);
    this.dobChangeHandler = this.dobChangeHandler.bind(this);
    this.cityChangeHandler = this.cityChangeHandler.bind(this);
    this.stateChangeHandler = this.stateChangeHandler.bind(this);
    this.countryChangeHandler = this.countryChangeHandler.bind(this);
    this.emailChangeHndler = this.emailChangeHandler.bind(this);
    this.phoneChangeHndler = this.phoneChangeHandler.bind(this);
    this.skillChangeHandler = this.skillChangeHandler.bind(this);

    this.submitHandler = this.submitHandler.bind(this);
    this.editHandler = this.editHandler.bind(this);
  }

  nameChangeHandler(event) {
    this.setState({ [event.target.name]: event.target.value });
  }

  messageChangeHandler(event) {
    this.setState({ [event.target.name]: event.target.value });
  }

  dobChangeHandler(event) {
    this.setState({ [event.target.name]: event.target.value });
  }

  cityChangeHandler(event) {
    this.setState({ [event.target.name]: event.target.value });
  }

  stateChangeHandler(event) {
    this.setState({ [event.target.name]: event.target.value });
  }

  emailChangeHandler(event) {
    this.setState({ [event.target.name]: event.target.value });
  }

  phoneChangeHandler(event) {
    this.setState({ [event.target.name]: event.target.value });
  }

  skillChangeHandler(event) {
    this.setState({ [event.target.name]: event.target.value });
  }

  countryChangeHandler(event) {
    this.setState({ [event.target.name]: event.target.value });
  }

  submitHandler(event) {
    event.preventDefault();
    let email = localStorage.getItem("email");
    let type;
    this.props
      .editstudentprofile({ type: "save", data: this.state })
      .then(data => console.log(data))
      .catch(err => console.log(err));
  }

  editHandler(event) {
    event.preventDefault();
    let email = localStorage.getItem("email");
    let type;
    this.props
      .editstudentprofile({ type: "edit", email: email })
      .then(data => {
        console.log("edit" + data.data.studentfullname);
        this.setState({
          message: data.data.message,
          name: data.data.studentfullname,
          dob: data.data.dob,
          city: data.data.city,
          state: data.data.state,
          country: data.data.country,
          email: data.data.email,
          phone: data.data.phone,
          skill: data.data.skillset
        });
        // .catch(err => {
        //   console.log(err);
      })
      // })
      .catch(err => console.log(err));
  }

  render() {
    console.log(localStorage.getItem("email"));
    return (
      <div>
        <form onSubmit={this.submitHandler} className="form-group">
          Career Objective:
          <textarea
            name="message"
            rows="1"
            cols="100"
            className="form-control"
            value={this.state.message || ""}
            onChange={this.messageChangeHandler}
          >
            Career Goals
          </textarea>
          <br />
          Name:
          <input
            type="text"
            name="name"
            placeholder="Name"
            //required
            className="form-control"
            value={this.state.name || ""}
            onChange={this.nameChangeHandler}
          />
          DateOfBirth:
          <input
            type="date"
            name="dob"
            className="form-control"
            value={this.state.dob || ""}
            onChange={this.dobChangeHandler}
          />
          City:
          <input
            type="text"
            name="city"
            className="form-control"
            placeholder="San Jose"
            value={this.state.city || ""}
            onChange={this.cityChangeHandler}
          />
          State:
          <input
            type="text"
            name="state"
            className="form-control"
            placeholder="California"
            value={this.state.state || ""}
            onChange={this.stateChangeHandler}
          />
          Country:
          <div className="form-group">
            <input
              type="text"
              name="country"
              className="form-control"
              placeholder="USA"
              value={this.state.country || ""}
              onChange={this.countryChangeHandler}
            />
          </div>
          <h3>Contact Information</h3>
          Email:
          <div className="form-group">
            <input
              type="text"
              name="email"
              className="form-control"
              placeholder="John.doe@gmail.com"
              value={this.state.email || ""}
              //onChange={this.emailChangeHandler}
            />
          </div>
          PhoneNumber:
          <div className="form-group">
            <input
              type="text"
              name="phone"
              className="form-control"
              placeholder="(123)456-8790"
              value={this.state.country || ""}
              onChange={this.phoneChangeHandler}
            />
          </div>
          Skills:
          <textarea
            name="skill"
            rows="1"
            cols="100"
            className="form-control"
            value={this.state.message || ""}
            onChange={this.skillChangeHandler}
          >
            Skills
          </textarea>
          <div className="form-group">
            <button
              type="submit"
              name="Submit"
              className="btn btn-primary btn-lg"
            >
              <b>Save</b>
            </button>
          </div>
          <div className="form-group">
            <button
              type="submit"
              name="edit"
              className="btn btn-primary btn-lg"
              onClick={this.editHandler}
            >
              <b>Show saved data</b>
            </button>
          </div>
        </form>
      </div>
    );
  }
}

export default connect(null, { editstudentprofile })(EditBasicDetails);
