import React, { Component } from "react";
import { Link } from "react-router-dom";
import cookie from "react-cookies";
//import { Redirect } from "react-router";
import {
  BrowserRouter as Router,
  Route,
  Redirect,
  Switch
} from "react-router-dom";

import axios from "axios";

//files
import EditBasicDetails from "../Components/Edit/EditBasicDetails";

class EditStudentProfile extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      profile: ""
    };
    this.myFunction = this.myFunction.bind(this);
    this.receiveState = this.receiveState.bind(this);
  }
  myFunction() {
    axios
      .get("http://127.0.0.1:4001/getStudentProfile", "data")
      .then(() => (this.state.profile = "profile"))
      .catch(err => console.log(err));
  }

  receiveState() {
    return this.state;
  }

  render() {
    return (
      <div>
        will have form to edit for {localStorage.getItem("email")}
        <div className="container">
          <div className="row">
            <div className=".col-sm-8">
              Basic Details:
              <EditBasicDetails
                myFunction={this.myFunction}
                receiveState={this.receiveState}
              />
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default EditStudentProfile;
